mapper.select:
SELECT COUNT(adcontentsno)
FROM adcontents
WHERE cateno= #{cateno}

mapper.update:
UPDATE adcontents 
SET youtube=#{youtube} 
WHERE adcontentsno=#{adcontentsno}

mapper.select:
SELECT adcontentsno, adminsno, cateno, title, content, recom, viewcnt, commentcnt, passwd, tag, rdate, file1, file1saved, thumb1, size1, youtube, id, r FROM ( SELECT adcontentsno, adminsno, cateno, title, content, recom, viewcnt, commentcnt, passwd, tag, rdate, file1, file1saved, thumb1, size1, youtube, id, rownum as r FROM ( SELECT adcontentsno, adminsno, cateno, title, content, recom, viewcnt, commentcnt, passwd, tag, rdate, file1, file1saved, thumb1, size1, youtube, id FROM adcontents
WHERE (UPPER(title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(content) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(tag) LIKE '%' || UPPER(#{word}) || '%')
ORDER BY adcontentsno DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

SELECT COUNT(*) as cnt FROM adcontents
WHERE cateno=#{cateno}
WHERE cateno=#{cateno} AND (UPPER(title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(content) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(tag) LIKE '%' || UPPER(#{word}) || '%')

SELECT adcontentsno, adminsno, cateno, title, content, recom, viewcnt, commentcnt, passwd, tag, rdate, file1, file1saved, thumb1, size1, youtube, id, r FROM ( SELECT adcontentsno, adminsno, cateno, title, content, recom, viewcnt, commentcnt, passwd, tag, rdate, file1, file1saved, thumb1, size1, youtube, id, rownum as r FROM ( SELECT adcontentsno, adminsno, cateno, title, content, recom, viewcnt, commentcnt, passwd, tag, rdate, file1, file1saved, thumb1, size1, youtube, id FROM adcontents
WHERE cateno=#{cateno}
WHERE cateno=#{cateno} AND (UPPER(title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(content) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(tag) LIKE '%' || UPPER(#{word}) || '%')
ORDER BY adcontentsno DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

SELECT adcontentsno, adminsno, cateno, title, content, recom, viewcnt, commentcnt, passwd, tag, rdate, file1, file1saved, thumb1, size1, youtube, id, r FROM ( SELECT adcontentsno, adminsno, cateno, title, content, recom, viewcnt, commentcnt, passwd, tag, rdate, file1, file1saved, thumb1, size1, youtube, id, rownum as r FROM ( SELECT adcontentsno, adminsno, cateno, title, content, recom, viewcnt, commentcnt, passwd, tag, rdate, file1, file1saved, thumb1, size1, youtube, id FROM adcontents
WHERE cateno=#{cateno}
ORDER BY adcontentsno DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

SELECT COUNT(*) as cnt 
FROM adcontents
WHERE adcontentsno = #{adcontentsno} AND passwd=#{passwd}

mapper.update:
UPDATE adcontents
SET title = #{title}, content = #{content},  tag = #{tag}
WHERE adcontentsno = #{adcontentsno}

UPDATE adcontents
SET file1 = #{file1}, file1saved = #{file1saved}, thumb1 = #{thumb1}, size1 = #{size1}
WHERE adcontentsno = #{adcontentsno}

UPDATE adcontents
SET viewcnt = viewcnt + 1
WHERE adcontentsno = #{adcontentsno}

UPDATE adcontents
SET recom = recom + 1
WHERE adcontentsno = #{adcontentsno}
