mapper.select:
SELECT COUNT(*) as cnt FROM health
WHERE htcno=#{htcno}
WHERE htcno=#{htcno} AND (UPPER(title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(explan) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')

SELECT healthno, adminsno, htcno, title, explan, recom, cnt, replycnt, rdate, file1, file1saved, thumb1, size1, map, youtube, r FROM ( SELECT healthno, adminsno, htcno, title, explan, recom, cnt, replycnt, rdate, file1, file1saved, thumb1, size1, map, youtube, rownum as r FROM ( SELECT healthno, adminsno, htcno, title, explan, recom, cnt, replycnt, rdate, file1, file1saved, thumb1, size1, map, youtube FROM health
WHERE htcno=#{htcno}
WHERE htcno=#{htcno} AND (UPPER(title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(explan) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')
ORDER BY healthno DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

SELECT COUNT(*) as cnt 
FROM health
WHERE healthno = #{healthno} AND passwd=#{passwd}

mapper.update:
UPDATE health
SET title=#{title}, explan=#{explan},  word=#{word}
WHERE healthno = #{healthno}

UPDATE health
SET file1=#{file1}, file1saved=#{file1saved}, thumb1=#{thumb1}, size1=#{size1}
WHERE healthno = #{healthno}

